function send_mail_vanzare_asteapta_publicare($idv){

    $vanzare=many_query("SELECT fc.denumire, fa.nume_asociatie, fu.full_name,vv.denumire_afacere
		FROM `vanzare` as vv 
		LEFT JOIN thf_users as fu on fu.id=vv.uid
		LEFT JOIN companie as fc on fc.id_companie=vv.companie_vanzare
		LEFT JOIN asociatii as fa on fa.id=fu.asoc_id
		WHERE vv.idv='".q($idv)."'	LIMIT 1");
    $afacere=$vanzare['denumire_afacere']?$vanzare['denumire_afacere']:'AFACERE FARA TITLU #'.$idv;
    $asociatie=$vanzare['nume_asociatie']?$vanzare['nume_asociatie']:'Asociatie nedefinita';
    $broker=$vanzare['full_name']?$vanzare['full_name']:'BROKER NEDEFINIT';


    $exceptie='';
    $to=one_query("
	SELECT GROUP_CONCAT(mail) as gc FROM `thf_users` 
		WHERE 
		access_level=2 AND 
		blocked_by_admin=0 AND
		asoc_id=(
			SELECT asoc_id FROM `thf_users` WHERE id=(SELECT uid FROM `vanzare` WHERE idv= '".q($idv)."' ) #iau asociatia din care face parte brokerul care a creat afacerea
		) ");
    if(strlen($to)<4){
        $to=one_query("SELECT GROUP_CONCAT(mail) as gc FROM `thf_users` WHERE `access_level` = 10 and confirmed=1 and blocked_by_admin=0 ");
        $exceptie="<strong>Nu s-a gasit manager pentru asociatia $asociatie din care face parte $broker </strong>";
    }
    //$exceptie.=' <br><em>TO: '.$to.'</em>';	$to='djthorr@gmail.com';

    $to=explode(',',$to);
    $link='https://brokers.trade-x.ro/add_afacere/?edit='.$idv;


    $subject="Afacerea '$afacere' asteapta aprobare";
    $body="
	Afacere: $afacere;<br>
	Asociatie: $asociatie;<br>
	Broker: $broker;<br>
	<br>
	<a href=\"$link\">$link</a><br>
	<br>
	$exceptie
	";
    $from='';
    foreach($GLOBALS['thf_mail_cfg'] as $from=>$a){break;}//load frist mail in config

    //return thf_mail($catre,$from,$subiect,$text,$file_path='',$reply_to='',$is_html=true);
    return thf_mail($to,$from,$subject,$body,$file_path='',$reply_to='',$is_html=true);
}







function send_email_cont_nou($idu){  global $promovari,$logo_base64, $users,$rol;
    $data = $users[$idu];


    $to = array($data['mail']);
    $subject = 'TRADEX - Cont nou fost creat';

    $text = nl2br('
    
    Buna ziua,

A fost creat un nou cont pe platforma TradeX de catre un administrator al platformei.

Datele utilizatorului sunt urmatoarele:

Nume prenume: '.$data['full_name'].'

Adresa de email: '.$data['mail'].'
Rol: '.$rol[$data['access_level']].'
Nume utilizator: '.$data['username'].'


Parola se defineste apasand <a href="https://brokers.trade-x.ro/reset_pass/">AICI</a>

 Cu consideratie,  Echipa TRADEX

             '.$logo_base64.'
    ');
    //prea($text); prea($to) ;  prea($data);  die;
    thf_mail($to,'support@trade-x.ro',$subject,$text,'','support@trade-x.ro',true);


    // prea($data);

}

